export declare const basicTemplate = "\nimport { DefaiElement } from '@defai/element-sdk';\n\nexport class BasicElement extends DefaiElement {\n  metadata = {\n    id: 'basic-element',\n    name: 'Basic Element',\n    version: '1.0.0',\n    author: 'Your Name',\n    description: 'A basic element template',\n    category: 'Utilities',\n    tags: ['basic', 'template'],\n    icon: '\uD83D\uDCE6',\n    minSize: { width: 300, height: 200 },\n    maxSize: { width: 800, height: 600 },\n    defaultSize: { width: 400, height: 300 },\n    tierRequired: 'free'\n  };\n  \n  permissions = {\n    network: false,\n    storage: true,\n    notifications: false,\n    clipboard: false,\n    canReceiveFrom: [],\n    canSendTo: [],\n    portfolio: false,\n    transactions: false,\n    aiChat: false,\n    wallet: false\n  };\n  \n  async onMount(context) {\n    console.log('Element mounted');\n  }\n  \n  async onUnmount() {\n    console.log('Element unmounted');\n  }\n  \n  async onResize(size) {\n    console.log('Element resized', size);\n  }\n  \n  async onSettingsChange(settings) {\n    console.log('Settings changed', settings);\n  }\n}\n";
export declare const reactTemplate = "\nimport React from 'react';\nimport { DefaiElement } from '@defai/element-sdk';\nimport { createRoot } from 'react-dom/client';\n\nclass ReactElement extends DefaiElement {\n  metadata = {\n    id: 'react-element',\n    name: 'React Element',\n    version: '1.0.0',\n    author: 'Your Name',\n    description: 'A React-based element template',\n    category: 'Utilities',\n    tags: ['react', 'template'],\n    icon: '\u269B\uFE0F',\n    minSize: { width: 300, height: 200 },\n    maxSize: { width: 800, height: 600 },\n    defaultSize: { width: 400, height: 300 },\n    tierRequired: 'free'\n  };\n  \n  permissions = {\n    network: false,\n    storage: true,\n    notifications: false,\n    clipboard: false,\n    canReceiveFrom: [],\n    canSendTo: [],\n    portfolio: false,\n    transactions: false,\n    aiChat: false,\n    wallet: false\n  };\n  \n  async onMount(context) {\n    const root = createRoot(document.getElementById('element-root'));\n    root.render(<App context={context} />);\n  }\n  \n  async onUnmount() {\n    // Cleanup\n  }\n  \n  async onResize(size) {\n    // Handle resize\n  }\n  \n  async onSettingsChange(settings) {\n    // Handle settings change\n  }\n}\n\nfunction App({ context }) {\n  return (\n    <div style={{ padding: '20px' }}>\n      <h1>Hello from React Element!</h1>\n      <p>User: {context.userId}</p>\n      <p>Tier: {context.userTier}</p>\n    </div>\n  );\n}\n\nexport default ReactElement;\n";
